import {
    SafeAreaView,
    View,
    Text,
    TouchableOpacity,
    StyleSheet,
} from 'react-native'
import { useState, useEffect } from 'react';


//구글 로그인
import * as Google from "expo-auth-session/providers/google";
import AsyncStorage from "@react-native-async-storage/async-storage";



//다크 모드
import DarkMode from '../components/styles/DarkMode'

//아이콘
import Icon from '../components/styles/Icons';




const Login = (props) => {
    // 다크 모드
    const [ui, setUI] = useState(true);

    // 안드로이드, 웹 클라이언트 아이디를 사용하여 인증 요청 보냄.
    // Google 인증 요청을 위한 훅 초기화
    // promptAsync: 인증 요청 보냄.
    const [request, response, promptAsync] = Google.useAuthRequest({
        webClientId: "733666115774-gjrhmv8ds8pterj29pa9mqkldeamq5f4.apps.googleusercontent.com",
        androidClientId: "733666115774-ugq6ojt0s0o76fcv8btk9cvt69uf0089.apps.googleusercontent.com",
    });

    // Google 인증 응답이 바뀔때마다 실행
    useEffect(() => {
        handleSignInWithGoogle();
    }, [response]);


    return (
        <SafeAreaView
            style={[
                ui != false ? DarkMode.lightPriceView : DarkMode.darkPriceView,
                {
                    flex: 1,
                    alignItems: 'center',
                }
            ]}
        >
            {/* 타이틀 세션 */}
            <View
                style={styles.titleSection}
            >
                <Text
                    style={[
                        ui != false ? DarkMode.lightMainText : DarkMode.darkMainText,
                        styles.titleText
                    ]}
                >
                    로그인
                </Text>
                <TouchableOpacity
                    style={styles.titleIcon}
                    onPress={() => {
                        props.navigation.navigate('MyPageMain')
                    }}
                >
                    <Icon
                        name='arrow-back-outline'
                        size={24}
                        color={ui != false ? 'black' : 'white'}
                    />
                </TouchableOpacity>
            </View>

            {/* 로그인 버튼 세션 */}
            <Button
                disabled={!request}
                title="Login"
                onPress={() => {
                    promptAsync();
                }}
            />
            <Button title="logout" onPress={() => handleLogout()} />

        </SafeAreaView>
    )
}


export default Login



const styles = StyleSheet.create({
    //타이틀 세션
    titleSection: {
        width: '91%',
    },
    titleIcon: {
        position: 'absolute',
        left: 0,
        top: 30,
    },
    titleText: {
        textAlign: 'center',
        fontSize: 20,
        fontWeight: 'bold',
        marginTop: 30,
    },



    //로그인 버튼 세션
    loginBtn: {
        width: '91%',
        height: 53,
        borderRadius: 10,
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'row',
    },
    btnText: {
        fontSize: 18,
        fontWeight: 'bold',
        lineHeight: 53,
        marginLeft: 9,
    },
})
